import {ChevronDownIcon, SortAscendingIcon, UsersIcon} from '@heroicons/react/solid'
import {Group, OrderModel} from './models/Order'
import {Popover, Transition} from '@headlessui/react'
/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {Fragment, useEffect, useState} from 'react'
import {shallowEqual, useDispatch, useSelector} from 'react-redux'
import AddManualModal from './modals/AddManualModal'
import AddModal from './modals/AddModal'
import EditMulti from './modals/EditMulti'
import {KTSVG, toAbsoluteUrl} from '../../../_metronic/helpers'
import OrderItem from './components/OrderItem'
import {RootState} from 'setup'
import {actions} from './redux/OrdersRedux'
import {FormGroup, Input, Label} from "reactstrap";

type Props = {
  className: string
  orders: OrderModel[]
}
const OrderList: React.FC<Props> = ({className, orders}) => {
  const dispatch = useDispatch()

  function format1(n:number) {
    return n.toFixed(0).replace(/./g, function(c, i, a) {
      return i > 0 && c !== "." && (a.length - i) % 3 === 0 ? "," + c : c;
    });
  }

  const [showAdd, setShowAdd] = useState(false)
  const [key, setKey] = useState("")
  const [keyrate, setKeyRate] = useState(0)
  const [showAddManual, setShowAddManual] = useState(false)
  const [showEditMulti, setShowEditMulti] = useState(false)
  const [totaltimeorder, setTotalTimeOrder] = useState(0)
  const [totaltimebuffedorder, setTotalTimeBuffedOrder] = useState(0)
  const [groupName, setGroupName] = useState('')
  const role: string =
    (useSelector<RootState>(({auth}) => auth.user?.role, shallowEqual) as string) || ''
  const user: string =
      (useSelector<RootState>(({auth}) => auth.user?.username, shallowEqual) as string) || ''
  const groups: Group[] =
    (useSelector<RootState>(({orders}) => orders.groups, shallowEqual) as Group[]) || []
  const currentGroup: Group =
    (useSelector<RootState>(({orders}) => orders.currentGroup, shallowEqual) as Group) || undefined
  useEffect(() => {
    async function getcounttimeorder() {
      let requestUrl=""
      if(role.indexOf("ROLE_ADMIN")>=0){
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebufforder';
      }else{
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebufforder?user='+user;
      }
      const response = await fetch(requestUrl, {
        method: 'get',
        headers: new Headers({
          'Authorization': '1',
          'Content-Type': 'application/x-www-form-urlencoded'
        })
      });
      const responseJson = await response.json();
      const {totaltimeorder} = responseJson;
      setTotalTimeOrder(totaltimeorder);
    }

    getcounttimeorder();

    async function getcounttimebuffedorder() {
      let requestUrl=""
      if(role.indexOf("ROLE_ADMIN")>=0){
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebuffedorder';
      }else{
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebuffedorder?user='+user;
      }
      const response = await fetch(requestUrl, {
        method: 'get',
        headers: new Headers({
          'Authorization': '1',
          'Content-Type': 'application/x-www-form-urlencoded'
        })
      });
      const responseJson = await response.json();
      const {totaltimebuffedorder} = responseJson;
      setTotalTimeBuffedOrder(totaltimebuffedorder);
    }

    getcounttimebuffedorder();
  }, [])
  const selectGroup = (item: Group) => {
    dispatch(actions.selectGroup(item))
  }

  const addGroup = () => {
    if (groupName.length === 0) {
      alert('vui lòng điền tên nhóm')
      return
    }
    dispatch(actions.addGroupRequest(groupName))
    setGroupName('')
  }
  const clickDeleteHandler = () => {
    const arr:string[]=[]
    orders.forEach(item=>{
      if(item.checked==true)
        arr.push(item.videoid)
    })
    const orderarr=arr.join(',')
    if (window.confirm("Bạn chắc chắn muốn xóa "+arr.length+" đơn!") == true) {
      dispatch(actions.deleteOrderRequest(orderarr,1))
    }
  }

  const clickDeleteOrderDoneHandler = () => {
    const arr:string[]=[]
    orders.forEach(item=>{
      if(item.checked==true)
        arr.push(item.videoid)
    })
    const orderarr=arr.join(',')
    if (window.confirm("Bạn chắc chắn muốn hoàn thành "+arr.length+" đơn!") == true) {
      dispatch(actions.deleteOrderRequest(orderarr,0))
    }
  }

  const isShowFixMulti = orders.find((item) => {
    if (item.checked) {
      return true
    }
    return false
  })

  return (
    <div className={`card ${className}`}>
      <div className="page-header" style={{backgroundColor:'#c0e1ce'}}>
        <div className="page-header__content">
          <div className="align-items-center row" style={{margin:10}}>
            <div className="col-lg-7 col-sm-12 c-order__header">
              <span  className='fw-bolder fs-3 mb-1'>Đơn đang chạy</span>
              <span  className='ml-2 fw-bold fs-7'>({orders.length} Video)</span>
              <p className="fw-bold c-order__list">
                <span style={{fontSize:12,marginTop:5}}>Tổng đặt: {format1(totaltimeorder==null?0:totaltimeorder)}h | Đã chạy: {format1(totaltimebuffedorder==null?0:totaltimebuffedorder)}h | Còn tồn: {format1(totaltimeorder-totaltimebuffedorder)}h</span>
              </p>
            </div>

            <div className="col-lg-5 col-sm-12 text-right">
              {isShowFixMulti && role === "ROLE_ADMIN"&&(
                  <button style={{margin:5}}
                          onClick={() => {
                            clickDeleteHandler()
                          }}
                          className='btn btn-light'
                  >
                    Xóa đơn
                  </button>
              )}
              {isShowFixMulti &&  (
                  <button
                          onClick={() => {
                            setShowEditMulti(true)
                          }}
                          className='btn btn-light'
                  >
                    Sửa nhiều
                  </button>
              )}

              {isShowFixMulti &&  (
                  <button style={{margin:5}}
                          onClick={() => {
                            clickDeleteOrderDoneHandler()
                          }}
                          className='btn btn-light'
                  >
                    Hoàn thành
                  </button>
              )}
              <button
                      onClick={() => {
                        setShowAddManual(true)
                      }}
                      className='btn btn-success'
              >
                Thêm đơn
              </button>
            </div>

          </div>
        </div>
        <div className="page-header__content">
        <div className="align-items-center row" style={{backgroundColor:"white",margin:10}}>
          <div style={{width:"30%"}}>
            <div className="pl-2 text-left">
              <Input style={{margin:10,width:'70%',maxWidth:200,height:40}}
                     id="note"
                     name="note"
                     value={key}
                     placeholder="Search..."
                     onChange={(e) => setKey(e.target.value)}
                     type="text"

              />
              <button style={{fontWeight:'bold',color:"black",backgroundColor:"#c0e1ce",height:40,width:'10%'}}
                      onClick={() => {
                        if(key.trim().length==0){
                          if(role.indexOf("ROLE_ADMIN")>=0){
                            dispatch(actions.requestOrders(''))
                          }else{
                            dispatch(actions.requestOrders(user))
                          }
                        }else{
                          if(role.indexOf("ROLE_ADMIN")>=0){
                            dispatch(actions.showordersfilter(key,''))
                          }else{
                            dispatch(actions.showordersfilter(key,user))
                          }
                        }
                        //setKey("")
                      }}
                      className='btn btn-sm'
              >
                Search
              </button>
            </div>

          </div>
          <div style={{width:"25%"}}>
            <div className="pl-2 text-left">

            </div>

          </div>
        </div>
        </div>
      </div>
      <div className='card-header border-0 pt-0'>
        <h3 className='card-title align-items-start flex-column'>
          <h3 className='card-title align-items-start'>
            <Input style={{marginTop:5,marginRight:5,width:250,height:40}}
                   id="note"
                   name="note"
                   value={key}
                   placeholder="Search..."
                   onChange={(e) => setKey(e.target.value)}
                   type="text"

            />
            <button style={{fontWeight:'bold',color:"white",backgroundColor:"#26695c",marginTop:5,height:40,textAlign:"center"}}
                    onClick={() => {
                      if(key.trim().length==0){
                        if(role.indexOf("ROLE_ADMIN")>=0){
                          dispatch(actions.requestOrders(''))
                        }else{
                          dispatch(actions.requestOrders(user))
                        }
                      }else{
                        if(role.indexOf("ROLE_ADMIN")>=0){
                          dispatch(actions.showordersfilter(key,''))
                        }else{
                          dispatch(actions.showordersfilter(key,user))
                        }
                      }
                      //setKey("")
                    }}
                    className='btn btn-sm'
            >
              Search
            </button>
            <Input style={{marginTop:5,marginLeft:5,width:100,height:40,fontSize:12,backgroundColor:'#c0e1ce',color:"black",textAlign:"center"}}
                   //onChange={(e) => setKeyRate(parseInt(e.target.value))}
                   onChange={(e) => {
                     setKeyRate(parseInt(e.target.value))
                  if(parseInt(e.target.value)==0){
                    if(role.indexOf("ROLE_ADMIN")>=0){
                      dispatch(actions.requestOrders(''))
                    }else{
                      dispatch(actions.requestOrders(user))
                    }
                  }else{
                    if(role.indexOf("ROLE_ADMIN")>=0){
                      dispatch(actions.showorderspercentfilter(parseInt(e.target.value),''))
                    }else{
                      dispatch(actions.showorderspercentfilter(parseInt(e.target.value),user))
                    }

                  }
                  //setKeyRate(keyrate)
                   }}
                className="form-control form-control-solid"
                type="select"
                value={keyrate}
            >
              <option key={0} value={0}>
                {"All"}
              </option>
              <option key={10} value={10}>
                {"10"}
              </option>
              <option key={20} value={20}>
                {"20"}
              </option>
              <option key={30} value={30}>
                {"30"}
              </option>
              <option key={40} value={40}>
                {"40"}
              </option>
              <option key={50} value={50}>
                {"50"}
              </option>
              <option key={60} value={60}>
                {"60"}
              </option>
              <option key={70} value={70}>
                {"70"}
              </option>
              <option key={80} value={80}>
                {"80"}
              </option>
              <option key={90} value={90}>
                {"90"}
              </option>
              <option key={100} value={100}>
                {"100"}
              </option>
            </Input>
            <span style={{marginTop:15,height:30,fontWeight:"bold",color:"black",textAlign:"center"}}>%</span>
          </h3>
        </h3>

        <div className='card-toolbar'>
          <div className='card-toolbar w-100'>
            {/* begin::Menu */}
            <Popover className='z-0 relative'>
              {({open}) => (
                <>
                  <Transition
                    as={Fragment}
                    enter='transition ease-out duration-200'
                    enterFrom='opacity-0 -translate-y-1'
                    enterTo='opacity-100 translate-y-0'
                    leave='transition ease-in duration-150'
                    leaveFrom='opacity-100 translate-y-0'
                    leaveTo='opacity-0 -translate-y-1'
                  >
                    <Popover.Panel className='absolute z-10 inset-x-0 transform shadow-lg'>
                      <div className='absolute inset-0 flex' aria-hidden='true'>
                        <div className='bg-white w-full' />
                      </div>
                      <div className='relative max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2'>
                        <nav
                          className='grid gap-y-10 px-4 py-8 bg-white'
                          aria-labelledby='solutions-heading'
                        >
                          <div>
                            <ul role='list' className='space-y-2 flex flex-col'>
                              {groups.map((item: Group) => {
                                return (
                                  <li
                                    key={item.id}
                                    onClick={() => {
                                      selectGroup(item)
                                    }}
                                    className='flex-1 w-96 cursor-pointer hover:text-blue focus:bg-blue'
                                  >
                                    {item.name}{' '}
                                    <button
                                      className='w-12 rounded-sm bg-danger text-white p-1 ml-5 cursor-pointer '
                                      onClick={() => {
                                        if (
                                          window.confirm('bạn có chắc chắn muốn xóa nhóm này') ==
                                          true
                                        ) {
                                          dispatch(actions.deleteGroupRequest(item.id))
                                        }
                                      }}
                                    >
                                      Xóa
                                    </button>
                                  </li>
                                )
                              })}
                            </ul>
                            <div>
                              <div className='mt-1 flex rounded-md shadow-sm w-80'>
                                <div className='relative flex items-stretch flex-grow focus-within:z-10'>
                                  <input
                                    value={groupName}
                                    onChange={(e) => setGroupName(e.target.value)}
                                    type='email'
                                    name='email'
                                    id='email'
                                    className='focus:ring-indigo-500 focus:border-indigo-500 block w-full rounded-none rounded-l-md sm:text-sm border-gray-300'
                                    placeholder='Nhập tên nhóm '
                                  />
                                </div>
                                <button
                                  type='button'
                                  onClick={addGroup}
                                  className='-ml-px relative inline-flex items-center space-x-2 px-4 py-2 border border-gray-300 text-sm font-medium rounded-r-md text-gray-700 bg-gray-50 hover:bg-gray-100 focus:outline-none focus:ring-1 focus:ring-indigo-500 focus:border-indigo-500'
                                >
                                  <SortAscendingIcon
                                    className='h-5 w-5 text-gray-400'
                                    aria-hidden='true'
                                  />
                                  <span>Thêm</span>
                                </button>
                              </div>
                            </div>
                          </div>
                        </nav>
                      </div>
                    </Popover.Panel>
                  </Transition>
                </>
              )}
            </Popover>
          </div>
        </div>
      </div>
      {/* end::Header */}
      {/* begin::Body */}
      <div className='card-body py-3'>
        <div className='table-responsive'>
          {/* begin::Table */}
          <table className='table table-row-bordered table-row-gray-100 align-middle gs-0 gy-3'>
            {/* begin::Table head */}
            <thead>
              <tr className='fw-bolder text-muted'>
                <th className='w-25px'>
                  <div className='form-check form-check-sm form-check-custom form-check-solid'>
                    <input
                      onChange={(evt) => {
                        dispatch(actions.checkedAllChange(evt.target.checked))
                      }}
                      className='form-check-input'
                      type='checkbox'
                      value='1'
                      data-kt-check='true'
                      data-kt-check-target='.widget-13-check'
                    />
                  </div>
                </th>
                <th className='min-w-10px text-sm'>
                  <span style={{fontSize:12,color:"black"}} className='text-sm'>STT</span>
                </th>
                <th className='min-w-10px text-sm'>
                  <span style={{fontSize:12,color:"black"}} className='text-sm'>Info</span>
                </th>
                <th className='min-w-10px text-sm'>
                  <span style={{fontSize:12,color:"black"}} className='text-sm'>Threads</span>
                </th>
                <th className='min-w-10px text-sm'>
                  <span style={{fontSize:12,color:"black"}} className='text-sm'>Status</span>
                </th>
                <th className='min-w-10px text-sm'>
                  <span style={{fontSize:12,color:"black"}} className='text-sm'>User</span>
                </th>
                <th   className='min-w-10px text-sm'>
                  <span style={{fontSize:12,color:"black"}} className='text-sm'>Note</span>
                </th>
              </tr>
            </thead>
            {/* end::Table head */}
            {/* begin::Table body */}
            <tbody>
              {orders &&
                orders.map((order: OrderModel, index: number) => {
                  if (currentGroup === undefined) {
                    return (
                      <OrderItem
                        index={index}
                        showEdit={role === 'ROLE_ADMIN'}
                        key={order.videoid}
                        item={order}
                      />
                    )
                  }
                  return null
                })}
            </tbody>
            {/* end::Table body */}
          </table>
          {/* end::Table */}
        </div>
        {/* end::Table container */}
      </div>
      {/* begin::Body */}
      {showAdd && (
        <AddModal
          show={true}
          close={() => {
            setShowAdd(false)
          }}
        />
      )}

      <EditMulti
        show={showEditMulti}
        close={() => {
          setShowEditMulti(false)
        }}
      />
      <AddManualModal
        show={showAddManual}
        close={() => {
          setShowAddManual(false)
        }}
      />
    </div>
  )
}

export {OrderList}
