import React, { useState, useEffect } from 'react'
import { KTSVG, toAbsoluteUrl } from '_metronic/helpers'
import { AccountModel } from 'app/modules/accounts/models/Account'
import { useDispatch } from 'react-redux'
import { actions } from '../../redux/AccountRedux'
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Button, Modal, ModalFooter,
    ModalHeader, ModalBody, Input
} from "reactstrap"



type Props = {
    item: AccountModel
}
const EditModal: React.FC<Props> = ({ item }) => {
    console.log("------item------", item)
    const dispatch = useDispatch()
    const [vpsoption, setvpsoption] = useState(item?.vpsoption)
    const [threads, setthreads] = useState(item?.state)
    const dismissModal = () => {
        dispatch(actions.clearCurrentAccount())
    }
    const updateUser = () => {
        dispatch(actions.requestUpdate({
            ...item,
            vpsoption:vpsoption
        }))
    }
    const handleChange = (e:any) => {
        setvpsoption(e.target.value)
    }


    return (
        <Modal isOpen={true}
            modalTransition={{ timeout: 500 }}>
            <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title">Update cho VPS IP:  {item?.vps}</h5>
                    <div className="btn btn-icon btn-sm btn-active-light-primary ms-2" aria-label="Close">
                        <span className="svg-icon svg-icon-2x"></span>
                    </div>
                </div>
                <div className="modal-body">
                    <p>Nhập số luồng</p>
                    <div className="input-group mb-5">
                        <input value={vpsoption} onChange={handleChange} type="number" className="form-control" placeholder="Nhập số point cho user" aria-label="Recipient's username" aria-describedby="basic-addon2" />
                        <span className="input-group-text" id="basic-addon2">thread</span>
                    </div>
                    <p>Chọn Option</p>
                    <div className="input-group mb-5">
                        <Input
                            onChange={(e) => setthreads(parseInt(e.target.value))}
                            className="form-control form-control-solid"
                            type="select"
                            value={threads}
                        >
                            <option key={1} value={1}>
                                {"Cheat"}
                            </option>
                            <option key={0} value={0}>
                                {"Buffh"}
                            </option>
                        </Input>
                    </div>
                </div>

                <div className="modal-footer">
                    <button type="button" onClick={dismissModal} className="btn btn-light" >Thoát</button>
                    <button type="button" onClick={updateUser} className="btn btn-primary">Lưu thông tin mới</button>
                </div>
            </div>

        </Modal>
    )
}

export default EditModal
