/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {useEffect, useState} from 'react'
import { KTSVG, toAbsoluteUrl } from '../../../_metronic/helpers'
import { AccountModel } from 'app/modules/accounts/models/Account'

import UserItem from './components/UserItem'
import {actions} from "./redux/AccountRedux";
import {shallowEqual, useDispatch, useSelector} from 'react-redux'
import {ComputerModel} from "../history/models/Account";
import {RootState} from "../../../setup";
import EditMulti from "./modals/EditMulti";
import RestartMulti from "./modals/RestartMulti";
type Props = {
  className: string,
  accounts: AccountModel[]
}

const UserList: React.FC<Props> = ({ className, accounts }) => {

  const dispatch = useDispatch()
  const [showEditMulti, setShowEditMulti] = useState(false)
  const [showRestartMulti, setShowRestartMulti] = useState(false)
  const [showAddManual, setShowAddManual] = useState(false)
  const [total_user, settotal_user] = useState(0)
  const [total_user_endtrial, settotal_user_endtrial] = useState(0)
  const [vpstpye, setvpstpye] = useState('')
  useEffect(() => {
    dispatch(actions.requestAccounts())
  } ,[])
  const isShowFixMulti = accounts.find((item) => {
    if (item.checked) {

      return true
    }
    return false
  })
  let total_waring=0;
  let total_thread=0;
  let total_view24h=0;
  let total_thread_set=0;
  let total_tool_warning=0;
  for(let i=0;i<accounts.length;i++){
    if(parseInt(accounts[i].timegettask)>30){
      total_tool_warning=total_tool_warning+1;
    }
    if((Date.now()-accounts[i].timecheck)/1000/60>6){
      total_waring=total_waring+1;
    }else{
      total_thread_set=total_thread_set+(accounts[i].threads-1);
      total_thread=total_thread+accounts[i].total;
    }
    total_view24h=total_view24h+accounts[i].view24h;
  }

  useEffect(() => {
    async function getcounts(){
      const requestUrl = 'http://accpremiumpostget-env.ap-southeast-1.elasticbeanstalk.com/gmails/countgmails';
      const response= await fetch(requestUrl,{
        method: 'get',
        headers: new Headers({
          'Authorization': '1',
          'Content-Type': 'application/x-www-form-urlencoded'
        })
      });
      const responseJson= await  response.json();
      const {counts}=responseJson;
      console.log(counts);
      settotal_user(counts);
    }
    async function getcountsbyendtrial(){
      const requestUrl = 'http://accpremiumpostget-env.ap-southeast-1.elasticbeanstalk.com/gmails/countgmailsbyendtrial';
      const response= await fetch(requestUrl,{
        method: 'get',
        headers: new Headers({
          'Authorization': '1',
          'Content-Type': 'application/x-www-form-urlencoded'
        })
      });
      const responseJson= await  response.json();
      const {counts}=responseJson;
      console.log(counts);
      settotal_user_endtrial(counts);
    }
    getcountsbyendtrial();
    getcounts();
  },[]);

  const clickDeleteHandler = () => {
    const arr:string[]=[]
    accounts.forEach(item=>{
      if(item.checked==true)
        arr.push(item.vps)
    })
    const vps=arr.join(',')
    if (window.confirm("Bạn chắc chắn muốn xóa "+arr.length+" VPS!") == true) {
      dispatch(actions.deleteVpsRequest(vps))
    }
  }

  return (

    <div className={`card ${className}`}>
      {/* begin::Header */}

      <div  className='card-header border-0 pt-5'>
        <div className="input-group mb-5">
          <input style={{fontWeight:'bold'}}
                 onChange={(e) => setvpstpye(e.target.value)}
                 value={vpstpye}  type="text"
                 className="form-control"
                 placeholder="Tìm kiếm VPS"
                 aria-label="Recipient's username"
                 aria-describedby="basic-addon2" />
        </div>
        <h3 className='card-title align-items-start flex-column'>
          <span style={{fontFamily:'monospace'}} className='card-label fw-bolder fs-3 mb-1'>Danh sách VPS </span>
          <span style={{color:'black',fontSize:20}} className='badge badge-success'>{accounts?accounts.length:0} vps running

            <span className='badge badge-warning' style={{ color:'black',margin:4}}>{total_waring} vps warning</span>
            <span className='badge badge-danger' style={{ color:'black',margin:4}}>{total_tool_warning} tool warning</span>
            <span className='badge badge-secondary' style={{ color:'black',margin:4}}>{total_thread} thread</span>
            <span className='badge badge-light-primary' style={{ color:'black',margin:4}}>{total_thread_set} thread set</span>
            <span className='badge ant-badge-status-cyan' style={{ color:'black',margin:4}}>{total_user_endtrial} sum user</span>
            <span className='badge badge-warning' style={{ color:'black',margin:4}}>{total_user} user</span>
            <span className='badge badge-danger' style={{ color:'black',margin:4}}>{total_user_endtrial-total_user} user die</span>
            </span>
        </h3>
        {isShowFixMulti && (
            <div>
                <button style={{fontWeight:'bold',margin:5}}
                        onClick={() => {
                          setShowEditMulti(true)
                        }}
                        className='btn btn-sm btn-primary'>
                  Chỉnh sửa
                </button>

              <button style={{fontWeight:'bold',margin:5}}
                      onClick={() => {
                        setShowRestartMulti(true)
                      }}
                      className='btn btn-sm btn-warning'>
                Restart
              </button>

                <button style={{fontWeight:'bold',margin:5}}
                        onClick={()=>clickDeleteHandler()}
                    className='btn btn-sm btn-danger'>
                  Xóa VPS
                </button>
            </div>

        )}
      </div>
      {/* end::Header */}
      {/* begin::Body */}
      <div className='card-body py-3'>
        {/* begin::Table container */}
        <div className='table-responsive'>
          {/* begin::Menu */}
          <table className='table table-row-dashed table-row-gray-300 align-middle gs-0 gy-4'>
            {/* begin::Table head */}
            <thead>
              <tr className='fw-bolder text-muted'>

                <th className='w-25px'>
                  <div className='form-check form-check-sm form-check-custom form-check-solid'>
                    <input
                        onChange={(evt) => {
                          dispatch(actions.checkedAllChange(evt.target.checked))
                        }}
                        className='form-check-input'
                        type='checkbox'
                        value='1'
                        data-kt-check='true'
                        data-kt-check-target='.widget-13-check'
                    />
                  </div>
                </th>
                <th className='w-25px'>STT</th>
                <th className='min-w-150px'>VPS IP</th>
                <th className='min-w-140px'>VPS Option</th>
                <th className='min-w-120px'>Trạng thái</th>
                <th className='min-w-100px text-end'>Hành động</th>
              </tr>
            </thead>
            {/* end::Table head */}
            {/* begin::Table body */}
            {accounts=accounts.filter(vps=>vps.vps.search(vpstpye)!=-1)}
            <tbody>
              {

                accounts&&accounts?.map((item: AccountModel,index:number) => {
                  return <UserItem key={item.id+index} item={item} index={index} />
                })
              }

            </tbody>
            {/* end::Table body */}
          </table>
          {/* end::Table */}
        </div>
        {/* end::Table container */}
      </div>
      {/* begin::Body */}
      <EditMulti
          show={showEditMulti}
          close={() => {
            setShowEditMulti(false)
          }}
      />
      <RestartMulti
          show={showRestartMulti}
          close={() => {
            setShowRestartMulti(false)
          }}
      />
    </div>
  )
}
export  { UserList }
