import React, { useState, useEffect } from 'react'
import { KTSVG, toAbsoluteUrl } from '_metronic/helpers'
import { OrderModel } from '../../models/Order'
import { useDispatch } from 'react-redux'
import { actions } from '../../redux/OrdersRedux'
import { getFunciton } from 'utils/ApiHelper'
type Props = {
    item: OrderModel,
    showEdit: boolean,
    index: number,
    //increase: number
}

const OrderItem: React.FC<Props> = ({ item, showEdit, index }) => {
    const [running, setRunning] = useState(0)
    const [success, setSuccess] = useState(0)
    const [loading, setLoading] = useState(false)




    const dispatch = useDispatch()
    const clickUpdateHandler = () => {
        dispatch(actions.showcurrentOrder(item))
    }
    const clickDeleteHandler = () => {
        if (window.confirm("bạn có chắc chắn muốn xóa đơn : "+item.videoid!) == true) {
            dispatch(actions.deleteOrderRequest(item.videoid))
        }
    }


    //const subNeedRun = item.view_need - (item.current_view - item.start_view)
    //const increase = item.current_view - item.start_view
    return (
        <tr style={{margin:100}}>

            <td>
                <span style={{fontWeight:'bold',fontSize:11,fontFamily:'cursive'}} className='text-muted fw-bold text-muted d-block text-sm'>{index+1}</span>
            </td>
            <td>

                    <span style={{ color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-danger 2'>
                        {item.videoid}
                    </span>
                    <span style={{ color:'black',margin:5,fontSize:11,backgroundColor:"#c0e1ce"}} className='badge badge-success 1'>{(item.optionbuff==10?'10min':item.optionbuff==30?'30':item.optionbuff==60?'60':item.optionbuff==0?'Auto':'120')+'|'+Math.round(Number(item.duration==null?0:item.duration)/60)+'min'}</span>
                    <span style={{ color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-warning'>{(Math.round(Number(item.timebuffhtotal==null?0:item.timebuffhtotal)/3600))+"/"+item.timebuff}</span>
                    <span style={{ color:'black',margin:5,fontSize:11,backgroundColor:"#c0e1ce"}} className='badge badge-dark'>{(item.viewtotal==null?0:item.viewtotal)}</span>
                    <span style={{ color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-success'>{Math.round((Math.round(Number(item.timebuffhtotal==null?0:item.timebuffhtotal)/3600)/item.timebuff*100))+'%'}</span>
                    <br/>
                {/*
                    <span style={{ color:'white',fontSize:11}} className='badge badge-danger'>{Math.round(Number(item.duration==null?0:item.duration)/60)+'min'}</span>
                    <span style={{ color:'white',margin:5,fontSize:11}} className='badge badge-success'>{(item.mobilerate==null?0:item.mobilerate)+'M'}</span>
                    <span style={{ color:'white',fontSize:11}} className='badge badge-dark'>{(item.homerate==null?0:item.homerate)+'H'}</span>
                    <span style={{ color:'white',margin:5,fontSize:11}} className='badge badge-dark'>{(item.searchrate==null?0:item.searchrate)+'SE'}</span>
                    <span style={{ color:'white',fontSize:11}} className='badge badge-dark'>{(item.suggestrate==null?0:item.suggestrate)+'SU'}</span>
                    <span style={{ color:'white',margin:5,fontSize:11}} className='badge badge-dark'>{(item.directrate==null?0:item.directrate)+'D'}</span>
                    <span style={{ color:'black',fontSize:11}} className='badge badge-warning'>{(item.likerate==null?0:item.likerate)+'L'}</span>
                    <span style={{ color:'black',margin:5,fontSize:11}} className='badge badge-warning'>{(item.commentrate==null?0:item.commentrate)+'C'}</span>
                */}
            </td>
            <td >
                {
                    item.enabled === 1 ? <span style={{color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-success'>R</span> :item.enabled === 2 ?
                        <span style={{color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-warning'>T1</span>:item.enabled === 3 ?
                            <span style={{color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-primary'>T2</span>:
                            <span style={{color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-danger'>S</span>

                }
                {
                    <span style={{color:'black',fontSize:11,margin:5}} >{new Date(item.insertdate).toLocaleDateString('vn-VN') +" "+ new Date(item.insertdate).toLocaleTimeString('vn-VN')}</span>
                }
            </td>
            <td>
                {
                    item.cancel === 0 ? <span style={{color:'white',fontSize:11,backgroundColor:"#26695c"}} className='badge badge-success'>Done</span> :
                        <span style={{color:'white',fontSize:11,backgroundColor:"#b7080f"}} className='badge badge-danger'>Cancel</span>

                }
                {
                    <span style={{color:'black',fontSize:11,margin:5}} >{new Date(item.enddate).toLocaleDateString('vn-VN') +" "+ new Date(item.enddate).toLocaleTimeString('vn-VN')}</span>
                }

            </td>
            <td>
               <span style={{color:'black',fontSize:11,fontWeight:'bold'}} >{item.note}</span>
            </td>
        </tr>
    )
}

export default OrderItem
