import React from 'react'
import { KTSVG, toAbsoluteUrl } from '_metronic/helpers'
import { ComputerModel } from 'app/modules/history/models/Account'
import {resetComputer} from 'app/modules/history/redux/AccountCRUD'
import moment from 'moment'
type Props = {
    item: ComputerModel,
    index: number
}

const ComputerItem: React.FC<Props> = ({ item, index }) => {

    const clickDeleteHandler= async()=>{
        if (window.confirm("bạn có chắc chắn muốn reset luồng trên máy này") == true) {
            const res:any = await resetComputer(item.computer_name)
            //dispatch(actions.deleteOrderRequest(item.channel_id))
        }
    }

    return (
        <tr>
            <td>
                <div className='d-flex flex-stack mb-2'>
                    <span className='text-muted me-2 fs-7 fw-bold'>{index + 1}</span>
                </div>
            </td>
            <td>
                <a href='#' className='text-sm text-dark fw-bolder text-hover-primary d-block mb-1 '>
                    {"Máy : " + item.computer_name}
                </a>
            </td>
            <td>
                <span className='text-muted fw-bold text-muted d-block text-sm'>{item.number_tab}</span>
            </td>
            <td>
                <span className='text-muted fw-bold text-muted d-block text-sm'>{item.time}</span>
            </td>
            <td>

                    <a href='#' onClick={clickDeleteHandler} className='btn btn-icon btn-bg-light btn-active-color-primary btn-sm'>
                        <KTSVG path='/media/icons/duotune/general/gen027.svg' className='svg-icon-3' />
                    </a>

            </td>
        </tr>
    )
}

export default ComputerItem
