import React, { useState, useEffect } from 'react'
import { KTSVG, toAbsoluteUrl } from '_metronic/helpers'
import { OrderModel } from '../../models/Order'
import { useDispatch } from 'react-redux'
import { actions } from '../../redux/OrdersRedux'
import { getFunciton } from 'utils/ApiHelper'
type Props = {
    item: OrderModel,
    showEdit: boolean,
    index: number
}

const OrderItem: React.FC<Props> = ({ item, showEdit, index }) => {
    const [running, setRunning] = useState(0)
    const [success, setSuccess] = useState(0)
    const [loading, setLoading] = useState(false)



    const reloadRunning = async () => {
        setLoading(true)
        let url = '/channel/countRunning?channel_id=' + item.channel_id;
        const res: any = await getFunciton(url)
        //console.log(res)
        if (res.data && res.data.countRunning != null) {
            setRunning(res.data.countRunning)
            setSuccess(res.data.countSuccess)
            //setIncrease(res.data.increase)
            //console.log("increase " + increase)
        } else {
            //console.log("sadassdsads")
        }
        setLoading(false)
    }
    useEffect(() => {
        const myTimeout = setTimeout(reloadRunning, Math.floor(Math.random() * 10));
        return () => {
            clearTimeout(myTimeout);
        }
    }, []);

    const dispatch = useDispatch()
    const clickUpdateHandler = () => {
        dispatch(actions.showcurrentOrder(item))
    }
    const clickDeleteHandler = () => {
        if (window.confirm("bạn có chắc chắn muốn xóa đơn này") == true) {
            dispatch(actions.deleteOrderRequest(item.channel_id))
        }
    }





    const subNeedRun = item.view_need - (item.current_view - item.start_view)
    const increase = item.current_view - item.start_view
    return (
        <tr>
            <td className='w-25px'>
                <div className='form-check form-check-sm form-check-custom form-check-solid'>
                    <input
                        onChange={(evt)=>{
                            dispatch(actions.checkedChange({
                                channel_id:item.channel_id,
                                checked:evt.target.checked
                            }))
                        }}
                        className='form-check-input'
                        type='checkbox'
                        value={1}
                        checked={item.checked}
                        data-kt-check='true'
                        data-kt-check-target='.widget-13-check'
                    />
                </div>
            </td>
            <td>
                <span className='text-muted fw-bold text-muted d-block text-sm'>{index}</span>
            </td>
            <td>
                <a href={'https://www.youtube.com/channel/' + item.channel_id} className='text-dark fw-bolder text-hover-primary text-sm'>
                    {item.user_id !== 1 && <span className='badge badge-light-danger'>USER</span>} {item.channel_id} {'view time : '+item.view_percent+"%"}
                </a>
                <span className={' fw-bold  d-block text-sm ' + (subNeedRun > 0 ? "text-warning" : "text-primary")}>{subNeedRun > 0 ? "Còn thiếu " : "Đã dư "} : {Math.abs(subNeedRun).toLocaleString() + "/" + item.view_need.toLocaleString()}</span>
            </td>
            <td>
                <span className='text-muted fw-bold text-muted d-block text-sm'>{item.note}</span>
            </td>
            <td>
                <a href='#' className='text-sm text-dark fw-bolder text-hover-primary d-block mb-1 '>
                    {item.insert_dateString}
                </a>
                <span className='text-muted fw-bold text-muted d-block text-sm'>Bđ : {item.start_view.toLocaleString()}</span>
                <span className='text-muted fw-bold text-muted d-block text-sm'>HT : {item.current_view.toLocaleString()}</span>
            </td>
            <td>
                <a href='#' className='text-sm text-dark fw-bolder text-hover-primary d-block mb-1 '>
                    {item.totalVideo}
                </a>
            </td>
            <td>
                <a href='#' className='text-dark fw-bolder text-hover-primary d-block mb-1 text-sm'>
                    {item.current_view.toLocaleString()}
                </a>
                <span className='text-muted fw-bold text-muted d-block text-sm'>
                    {(item.current_view - item.start_view).toLocaleString()}
                </span>
            </td>
            <td className='text-dark fw-bolder text-hover-primary text-sm'>
                {loading ? <span className='text-muted fw-bold text-muted d-block fs-7'>
                    {"Đang lấy dữ liệu"}
                </span> :
                    <>
                        <a href='#' className='text-dark fw-bolder text-hover-primary d-block mb-1 text-sm'>
                            {success.toLocaleString()}
                        </a>
                        <span className='text-muted fw-bold text-muted d-block fs-7'>{increase.toLocaleString()}</span>
                    </>
                }
            </td>
            <td className='text-dark fw-bolder text-hover-primary text-sm'>
                {loading ? <span className='text-muted fw-bold text-muted d-block fs-7'>
                    {"Đang lấy dữ liệu"}
                </span> :
                    <a href='#' className='text-dark fw-bolder text-hover-primary d-block mb-1 text-sm'>
                        <span className='badge badge-light-success me-3'>{(increase / success * 100).toFixed(2) + " %"}</span>
                    </a>
                }
            </td>
            <td className='text-dark fw-bolder text-hover-primary text-sm'>
                {loading ? <span className='text-muted fw-bold text-muted d-block text-sm'>
                    {"Đang lấy dữ liệu"}
                </span> :
                    <span className='badge badge-light-warning'>{running}/{item.max_thread}</span>
                }
            </td>
            <td>
                {
                    item.priority === 1 && <span className='badge badge-light-primary me-3'>Ưu tiên</span>
                }
                {
                    item.enabled === 1 ? <span className='badge badge-light-success'>Đang chạy</span> :
                        <span className='badge badge-light-danger'>Ngừng</span>
                }
            </td>
            {
                 <td className='text-end justify-items-center flex items-center'>
                    <a href='#' onClick={clickUpdateHandler} className='btn btn-icon btn-bg-light btn-active-color-primary btn-sm mr-5'>
                        <KTSVG path='/media/icons/duotune/art/art005.svg' className='svg-icon-3' />
                    </a>
                    <a href='#' onClick={clickDeleteHandler} className='btn btn-icon btn-bg-light btn-active-color-primary btn-sm'>
                        <KTSVG path='/media/icons/duotune/general/gen027.svg' className='svg-icon-3' />
                    </a>
                </td>
            }

        </tr>
    )
}

export default OrderItem
