/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {useEffect} from 'react'
import { KTSVG, toAbsoluteUrl } from '../../../_metronic/helpers'
import { AccountModel } from 'app/modules/accounts/models/Account'
import UserItem from './components/UserItem'
import {shallowEqual, useDispatch, useSelector} from "react-redux";
import {ChannelStaticModel} from "../history/models/Account";
import {RootState} from "../../../setup";
import { actions } from './redux/AccountRedux'
type Props = {
  className: string,
  accounts?: AccountModel[]
}

const UserList: React.FC<Props> = ({ className }) => {
  const dispatch = useDispatch()
  useEffect(() => {
    dispatch(actions.fulfillAccounts(accounts))
  }, [])
  const accounts:AccountModel[] = useSelector<RootState>(({ accounts }) => accounts.accounts, shallowEqual) as AccountModel[] || []
  return (
    <div className={`card ${className}`}>
      {/* begin::Header */}
      <div className='card-header border-0 pt-5'>
        <h3 className='card-title align-items-start flex-column'>
          <span className='card-label fw-bolder fs-3 mb-1'>Danh sách VPS </span>
          <span className='text-muted mt-1 fw-bold fs-7'>Có {accounts?accounts.length:0} users</span>
        </h3>
      </div>
      {/* end::Header */}
      {/* begin::Body */}
      <div className='card-body py-3'>
        {/* begin::Table container */}
        <div className='table-responsive'>
          {/* begin::Table */}
          <table className='table table-row-dashed table-row-gray-300 align-middle gs-0 gy-4'>
            {/* begin::Table head */}
            <thead>
              <tr className='fw-bolder text-muted'>
                <th className='w-25px'>
                  <div className='form-check form-check-sm form-check-custom form-check-solid'>
                    <input
                      className='form-check-input'
                      type='checkbox'
                      value='1'
                      data-kt-check='true'
                      data-kt-check-target='.widget-9-check'
                    />
                  </div>
                </th>
                <th className='min-w-150px'>VPS</th>
                <th className='min-w-140px'>Option</th>
                <th className='min-w-120px'>State</th>
                <th className='min-w-100px text-end'>Hành động</th>
              </tr>
            </thead>
            {/* end::Table head */}
            {/* begin::Table body */}
            <tbody>
              {
                accounts&&accounts?.map((item: AccountModel) => {
                  return <UserItem key={item.id} item={item} />
                })
              }

            </tbody>
            {/* end::Table body */}
          </table>
          {/* end::Table */}
        </div>
        {/* end::Table container */}
      </div>
      {/* begin::Body */}
    </div>
  )
}

export { UserList }
