import React from 'react'
import { KTSVG, toAbsoluteUrl } from '_metronic/helpers'
import { ComputerModel } from 'app/modules/history/models/Account'
import {resetComputer} from 'app/modules/history/redux/AccountCRUD'
import moment from 'moment'
type Props = {
    item: ComputerModel,
    index: number
}

const ComputerItem: React.FC<Props> = ({ item, index }) => {

    const clickDeleteHandler= async()=>{
        if (window.confirm("bạn có chắc chắn muốn reset luồng trên VPS này") == true) {
            const res:any = await resetComputer(item.vps)
            //dispatch(actions.deleteOrderRequest(item.channel_id))
        }
    }

    return (
        <tr >
            <td>
                <div className='d-flex flex-stack mb-2'>
                    <span className='text-muted me-2 fs-7 fw-bold'>{index + 1}</span>
                </div>
            </td>
            <td>
                <a href='#' style={{textDecorationLine:'none',fontSize:13}} className='text-sm text-dark fw-bolder text-hover-primary d-block mb-1 '>
                    {item.vps}
                </a>
            </td>
            <td>
                <span style={{fontSize:11,color:'black'}} className='badge badge-warning'>{item.total}</span>
            </td>
            <td>

                <span className={parseInt(item.time)<60?'badge badge-success':'badge badge-danger'}>{item.time=='0'?"Vừa xong":item.time+ " phút trước"}</span>
            </td>
            <td>

                    <a href='#' onClick={clickDeleteHandler} className='btn btn-icon  btn-active-color-primary btn-sm'>
                    <KTSVG path='/media/icons/duotune/general/gen027.svg' className={Number(item.time)>60?'svg-icon-danger':'svg-icon-3'} />
                    </a>

            </td>
        </tr>
    )
}

export default ComputerItem
