import { actions } from '../../redux/OrdersRedux'
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Button, Modal,
    Form, FormGroup, Input, Label, ModalFooter,
    ModalHeader, ModalBody
} from "reactstrap"
import {useDispatch, useSelector, shallowEqual } from 'react-redux'
import { RootState } from 'setup'
import { Group } from '../../models/Order'


type Props = {
    show: boolean
    close: () => void
}
const EditMulti: React.FC<Props> = ({ show,close }) => {
    const role: string = useSelector<RootState>(({ auth }) => auth.user?.role, shallowEqual) as string || ""
    const balance: number = useSelector<RootState>(({ auth }) => auth.user?.balance, shallowEqual) as number || 0
    const adding: boolean = useSelector<RootState>(({ orders }) => orders.adding, shallowEqual) as boolean || false
    const groups: Group[] = useSelector<RootState>(({ orders }) => orders.groups, shallowEqual) as Group[] || []
    const orders: OrderModel[] = useSelector<RootState>(({ orders }) => orders.orders, shallowEqual) as OrderModel[] || []

    const dispatch = useDispatch()
    const [max_thread, setMax_thread] = useState(5)
    //
    const [view_need, setView_need] = useState(1000)
    const [premium_rate,setPremium_rate]=useState(5)
    const [view_percent,setView_percent]=useState(30)
    //
    const [home_rate,setHome_rate]=useState(25)
    const [search_rate,setSearch_rate]=useState(25)
    const [suggest_rate,setSuggest_rate]=useState(25)
    const [direct_rate,setDirect_rate]=useState(25)
    //
    const [like_rate,setLike_rate]=useState(25)
    const [comment_rate,setComment_rate]=useState(25)
    const [mobile_rate,setMobile_rate]=useState(0)
    //
    const [note, setNote] = useState("")
    const [keyword, setKeyword] = useState("")
    const [comment_list, setComment_list] = useState("")
    const [group_id, setGroup] = useState(0)
    //
    const [enabled,setEnabled]=useState(1)


    const dismissModal = () => {
        dispatch(actions.clearcurrentOrder())
    }

    const submit = () => {
        const arr:string[]=[]
        orders.forEach(item=>{
            arr.push(item.channel_id)
        })
        const channel_id=arr.join('\n')
        if (view_need % 100 !== 0 || view_need ==0) {
            alert("Số sub cần chạy phải chia hết cho 100")
            return
        }
        if(home_rate+search_rate+suggest_rate+direct_rate !=100){
            alert("Tổng nguồn view không đúng")
            return
        }
        if(like_rate>100||like_rate<0){
            alert("Tỉ lệ like không đúng")
            return
        }
        if(view_percent>100||view_percent<0){
            alert("phần trăm xem video không đúng")
            return
        }
        if(comment_rate>100||comment_rate<0){
            alert("Tỉ lệ comment không đúng")
            return
        }

        dispatch(actions.editMultiOrderRequest({
            channel_id,
            home_rate,
            //note,
            direct_rate,
            //view_need,
            //comment_rate,
            //mobile_rate,
            search_rate,
            enabled,
            max_thread,
            //group_id,
            view_percent,
            //like_rate,
            //comment_list,
            suggest_rate,
            //premium_rate,
            //keyword
        }))

    }

    useEffect(() => {
        if (!adding) {
            close()
        }
    }, [adding])

    return (
        <Modal isOpen={show}
            modalTransition={{ timeout: 500 }}>
            <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title">Sửa nhiệm vụ</h5>
                    <div className="btn btn-icon btn-sm btn-active-light-primary ms-2" aria-label="Close">
                        <span className="svg-icon svg-icon-2x"></span>
                    </div>
                </div>
                <div className="modal-body">
                    <p>Cài đặt danh sách đơn</p>
                    <Form>
                        <p>Số lượng chạy</p>
                        <div className='flex flex-row justify-between space-x-3'>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    View max
                                </Label>
                                <Input
                                    id="view_need"
                                    name="view_need"
                                    value={view_need}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    onChange={(e) => setView_need(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Luồng max
                                </Label>
                                <Input
                                    id="max_thread"
                                    name="max_thread"
                                    value={max_thread}
                                    disabled={role === "ROLE_ADMIN" ? false : true}
                                    onChange={(e) => setMax_thread(parseInt(e.target.value))}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Tỉ lệ Premium (%)
                                </Label>
                                <Input
                                    id="premium_rate"
                                    name="premium_rate"
                                    value={premium_rate}
                                    disabled={role === "ROLE_ADMIN" ? false : true}
                                    onChange={(e) => setPremium_rate(parseInt(e.target.value))}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    type="number"
                                />
                            </FormGroup>
                        </div>
                        <p>Nguồn view tổng = 100%</p>
                        <div className='flex flex-row justify-between space-x-3'>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Home 
                                </Label>
                                <Input
                                    id="home_rate"
                                    name="home_rate"
                                    value={home_rate}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    onChange={(e) => setHome_rate(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Search
                                </Label>
                                <Input
                                    id="search_rate"
                                    name="search_rate"
                                    value={search_rate}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    onChange={(e) => setSearch_rate(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Suggest
                                </Label>
                                <Input
                                    id="suggest_rate"
                                    name="suggest_rate"
                                    value={suggest_rate}
                                    disabled={role === "ROLE_ADMIN" ? false : true}
                                    onChange={(e) => setSuggest_rate(parseInt(e.target.value))}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Direct
                                </Label>
                                <Input
                                    id="direct_rate"
                                    name="direct_rate"
                                    value={direct_rate}
                                    disabled={role === "ROLE_ADMIN" ? false : true}
                                    onChange={(e) => setDirect_rate(parseInt(e.target.value))}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    type="number"
                                />
                            </FormGroup>
                        </div>
                        <p>Cài đặt tương tác</p>
                        <div className='flex flex-row justify-between space-x-3'>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Tỉ lệ Like 
                                </Label>
                                <Input
                                    id="like_rate"
                                    name="like_rate"
                                    value={like_rate}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    onChange={(e) => setLike_rate(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                    Tỉ lệ comment
                                </Label>
                                <Input
                                    id="comment_rate"
                                    name="comment_rate"
                                    value={comment_rate}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    onChange={(e) => setComment_rate(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                   Mobile rate (%)
                                </Label>
                                <Input
                                    id="mobile_rate"
                                    name="mobile_rate"
                                    value={mobile_rate}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 1000"
                                    onChange={(e) => setMobile_rate(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                            <FormGroup>
                                <Label for="exampleEmail" className="required form-label">
                                   View Time(%)
                                </Label>
                                <Input
                                    id="mobile_rate"
                                    name="mobile_rate"
                                    value={view_percent}
                                    className="form-control form-control-solid"
                                    placeholder="ví dụ : 100"
                                    onChange={(e) => setView_percent(parseInt(e.target.value))}
                                    type="number"
                                />
                            </FormGroup>
                        </div>
                        <FormGroup>
                            <Label for="exampleEmail" className="required form-label">
                                Ghi chú
                            </Label>
                            <Input
                                id="note"
                                name="note"
                                value={note}
                                onChange={(e) => setNote(e.target.value)}
                                className="form-control form-control-solid"
                                type={"text"}
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="exampleEmail" className="required form-label">
                                Danh sách comment
                            </Label>
                            <Input
                                id="comment_list"
                                name="comment_list"
                                value={comment_list}
                                onChange={(e) => setComment_list(e.target.value)}
                                className="form-control form-control-solid"
                                style={{ height: "200px" }}
                                type={"textarea"}
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="exampleEmail" className="required form-label">
                                Từ khóa
                            </Label>
                            <Input
                                id="keyword"
                                name="keyword"
                                value={keyword}
                                onChange={(e) => setKeyword(e.target.value)}
                                className="form-control form-control-solid"
                                type={"text"}
                            />
                        </FormGroup>
                        <FormGroup>
                            <Label for="exampleEmail" className="required form-label">
                                Nhóm
                            </Label>
                            <Input
                                onChange={(e) => setGroup(parseInt(e.target.value))}
                                className="form-control form-control-solid"
                                type="select"
                                value={group_id}
                            >
                                <option key={-1} value={-1}>
                                    {"Không có nhóm"}
                                </option>
                                {
                                    groups.map((item: Group) => {
                                        return <option key={item.id} value={item.id}>
                                            {item.name}
                                        </option>
                                    })
                                }
                            </Input>
                        </FormGroup>
                        <FormGroup>
                            <Label for="exampleEmail" className="required form-label">
                                Trạng thái
                            </Label>
                            <Input
                                onChange={(e) => setEnabled(parseInt(e.target.value))}
                                className="form-control form-control-solid"
                                type="select"
                                value={enabled}
                            >
                                <option key={1} value={1}>
                                    {"Chạy"}
                                </option>
                                <option key={0} value={0}>
                                    {"Ngừng"}
                                </option>
                            </Input>
                        </FormGroup>
                    </Form>
                </div>
                <div className="modal-footer">
                    <button type="button" onClick={close} className="btn btn-light" >Thoát</button>
                    <button disabled={adding} type="button" onClick={submit} className="btn btn-primary">{adding ? "Chờ" : "Cập nhật"}</button>
                </div>
            </div>
        </Modal>
    )
}

export default EditMulti
