/* eslint-disable jsx-a11y/anchor-is-valid */
import React, {useEffect, useState} from 'react'
import {ChannelStaticModel, HistoryModel} from 'app/modules/history/models/Account'
import ChannelStaticItem from './components/ChannelStaticItem'
import { useDispatch, useSelector, shallowEqual } from 'react-redux'
import { actions } from './redux/AccountRedux'
import { RootState } from 'setup'
import HistoryItem from "./components/HistoryItem";
type Props = {
  className: string,
}

const StaticList: React.FC<Props> = ({ className }) => {
  const dispatch = useDispatch()
  const [totaltimeorder, setTotalTimeOrder] = useState(0)
  const [totaltimebuffedorder, setTotalTimeBuffedOrder] = useState(0)
  const role: string =
      (useSelector<RootState>(({auth}) => auth.user?.role, shallowEqual) as string) || ''
  const user: string =
      (useSelector<RootState>(({auth}) => auth.user?.username, shallowEqual) as string) || ''
  useEffect(() => {
    async function getcounttimeorder(){
      let requestUrl=""
      if(role.indexOf("ROLE_ADMIN")>=0){
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebufforder';
      }else{
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebufforder?user='+user;
      }

      const response= await fetch(requestUrl,{
        method: 'get',
        headers: new Headers({
          'Authorization': '1',
          'Content-Type': 'application/x-www-form-urlencoded'
        })
      });
      const responseJson= await  response.json();
      const {totaltimeorder}=responseJson;
      setTotalTimeOrder(totaltimeorder);
    }
    getcounttimeorder();
    async function getcounttimebuffedorder(){
      let requestUrl=""
      if(role.indexOf("ROLE_ADMIN")>=0){
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebuffedorder';
      }else{
        requestUrl = 'http://171.244.61.61/videobuffh/getcounttimebuffedorder?user='+user;
      }
      const response= await fetch(requestUrl,{
        method: 'get',
        headers: new Headers({
          'Authorization': '1',
          'Content-Type': 'application/x-www-form-urlencoded'
        })
      });
      const responseJson= await  response.json();
      const {totaltimebuffedorder}=responseJson;
      setTotalTimeBuffedOrder(totaltimebuffedorder);
    }
    getcounttimebuffedorder();
    if(role.indexOf("ROLE_ADMIN")>=0){
      dispatch(actions.requestStatics(''))
    }else{
      dispatch(actions.requestStatics(user))
    }
  }, [])

  const statics:HistoryModel[] = useSelector<RootState>(({ histories }) => histories.statics, shallowEqual) as HistoryModel[] || []

  return (
    <div className={`card ${className}`}>
      {/* begin::Header */}
      <div className="page-header" style={{backgroundColor:'#c0e1ce'}}>
        <div className="page-header__content">
          <div className="align-items-center row" style={{margin:10}}>
            <div className="col-lg-8 col-sm-12 c-order__header">
              <span  className='fw-bolder fs-3 mb-1'>Thống kê</span>
              <p className="fw-bold c-order__list">
                <span style={{fontSize:12,marginTop:5}}>Tổng đặt: {totaltimeorder}h | Đã chạy: {totaltimebuffedorder}h | Còn tồn: {totaltimeorder-totaltimebuffedorder}h</span>
              </p>
            </div>
          </div>
        </div>
      </div>
      {/* end::Header */}
      {/* begin::Body */}
      <div className='card-body py-3'>
        {/* begin::Table container */}
        <div className='table-responsive'>
          {/* begin::Table */}
          <table className='table table-row-dashed table-row-gray-300 align-middle gs-0 gy-4'>
            {/* begin::Table head */}
            <thead>
              <tr>
                <th className='min-w-30px' style={{fontWeight:"bold",fontSize:12}}>#</th>
                <th className='min-w-100px'  style={{fontWeight:"bold",fontSize:12}}>Date</th>
                <th className='min-w-100px'  style={{fontWeight:"bold",fontSize:12}}>Time buff </th>
                <th className='min-w-100px'  style={{fontWeight:"bold",fontSize:12}}>View buff </th>

              </tr>
            </thead>
            {/* end::Table head */}
            {/* begin::Table body */}
            <tbody>
              {
                statics&&statics?.map((item: HistoryModel,index:number) => {
                  return <HistoryItem key={+index} item={item} index={index}/>
                })
              }

            </tbody>
            {/* end::Table body */}
          </table>
          {/* end::Table */}
        </div>
        {/* end::Table container */}
      </div>
      {/* begin::Body */}
    </div>
  )
}

export { StaticList }
