/* eslint-disable jsx-a11y/anchor-is-valid */
import React,{useEffect} from 'react'
import { ChannelStaticModel } from 'app/modules/history/models/Account'
import ChannelStaticItem from './components/ChannelStaticItem'
import { useDispatch, useSelector, shallowEqual } from 'react-redux'
import { actions } from './redux/AccountRedux'
import { RootState } from 'setup'
type Props = {
  className: string,
}

const StaticList: React.FC<Props> = ({ className }) => {
  const dispatch = useDispatch()


  useEffect(() => {
    dispatch(actions.requestStatics())
  }, [])

  const statics:ChannelStaticModel[] = useSelector<RootState>(({ histories }) => histories.statics, shallowEqual) as ChannelStaticModel[] || []


  return (
    <div className={`card ${className}`}>
      {/* begin::Header */}
      <div className='card-header border-0 pt-5'>
        <h3 className='card-title align-items-start flex-column'>
          <span className='card-label fw-bolder fs-3 mb-1'>Danh sách máy đang chạy</span>
          <span className='text-muted mt-1 fw-bold fs-7'>Có {statics?statics.length:0} máy</span>
        </h3>
      </div>
      {/* end::Header */}
      {/* begin::Body */}
      <div className='card-body py-3'>
        {/* begin::Table container */}
        <div className='table-responsive'>
          {/* begin::Table */}
          <table className='table table-row-dashed table-row-gray-300 align-middle gs-0 gy-4'>
            {/* begin::Table head */}
            <thead>
              <tr>
                <th className='min-w-30px'>#</th>
                <th className='min-w-140px'>Tên máy</th>
                <th className='min-w-140px'>Số luồng hoàn thành</th>
                <th className='min-w-140px'>Số luồng đang chạy</th>
                <th className='min-w-140px'>Nguồn</th>
                <th className='min-w-140px'>Premium</th>
                <th className='min-w-140px'>Lần cuối</th>
              </tr>
            </thead>
            {/* end::Table head */}
            {/* begin::Table body */}
            <tbody>
              {
                statics&&statics?.map((item: ChannelStaticModel,index:number) => {
                  return <ChannelStaticItem key={"channel-static-"+index} item={item} index={index}/>
                })
              }

            </tbody>
            {/* end::Table body */}
          </table>
          {/* end::Table */}
        </div>
        {/* end::Table container */}
      </div>
      {/* begin::Body */}
    </div>
  )
}

export { StaticList }
