import React, { useState, useEffect } from 'react'
import { KTSVG, toAbsoluteUrl } from '_metronic/helpers'
import { AccountModel } from 'app/modules/accounts/models/Account'
import {shallowEqual, useDispatch, useSelector} from 'react-redux'
import { actions } from '../../redux/AccountRedux'
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Button, Modal, ModalFooter,
    ModalHeader, ModalBody, Input
} from "reactstrap"
import {RootState} from "../../../../../setup";
import {Group, OrderModel} from "../../../orders/models/Order";



type Props = {
    show: boolean
    close: () => void
}
const EditMulti: React.FC<Props> = ({ show,close }) => {
    //console.log("------item------", item)
    const dispatch = useDispatch()
    const role: string = useSelector<RootState>(({ auth }) => auth.user?.role, shallowEqual) as string || ""
    const balance: number = useSelector<RootState>(({ auth }) => auth.user?.balance, shallowEqual) as number || 0
    const adding: boolean = useSelector<RootState>(({ accounts }) => accounts.adding, shallowEqual) as boolean || false
    const accounts: AccountModel[] = useSelector<RootState>(({ accounts }) => accounts.accounts, shallowEqual) as OrderModel[] || []


    const [vpsoption, setvpsoption] = useState('Cheat')
    const [threads, setthreads] = useState(100)
    const dismissModal = () => {
        dispatch(actions.clearCurrentAccount())
    }
    const updateUser = () => {
        dispatch(actions.editMultiOrderRequest({
            vps:v,
            vpsoption:vpsoption,
            threads:threads
        }))
    }
    const submit = () => {
        const arr:string[]=[]
        orders.forEach(item=>{
            if(item.checked==true)
                arr.push(item.channel_id)
        })
        const channel_id=arr.join('\n')
        if (view_need % 100 !== 0 || view_need ==0) {
            alert("Số sub cần chạy phải chia hết cho 100")
            return
        }
        if(home_rate+search_rate+suggest_rate+direct_rate !=100){
            alert("Tổng nguồn view không đúng")
            return
        }
        if(like_rate>100||like_rate<0){
            alert("Tỉ lệ like không đúng")
            return
        }
        if(view_percent>100||view_percent<0){
            alert("phần trăm xem video không đúng")
            return
        }
        if(comment_rate>100||comment_rate<0){
            alert("Tỉ lệ comment không đúng")
            return
        }

        dispatch(actions.editMultiOrderRequest({
            channel_id,
            home_rate,
            direct_rate,
            search_rate,
            enabled,
            max_thread,
            view_percent,
            suggest_rate
        }))

    }


    return (
        <Modal isOpen={true}
            modalTransition={{ timeout: 500 }}>
            <div className="modal-content">
                <div className="modal-header">
                    <h5 className="modal-title">Update VPS</h5>
                    <div className="btn btn-icon btn-sm btn-active-light-primary ms-2" aria-label="Close">
                        <span className="svg-icon svg-icon-2x"></span>
                    </div>
                </div>
                <div className="modal-body">
                    <p>Nhập số luồng</p>
                    <div className="input-group mb-5">
                        <input value={threads} onChange={handleChange} type="number" className="form-control" placeholder="Nhập số point cho user" aria-label="Recipient's username" aria-describedby="basic-addon2" />
                        <span className="input-group-text" id="basic-addon2">thread</span>
                    </div>
                    <p>Chọn Option</p>
                    <div className="input-group mb-5">
                        <Input
                            onChange={(e) => setvpsoption(e.target.value)}
                            className="form-control form-control-solid"
                            type="select"
                            value={vpsoption}
                        >
                            <option key={1} value={'Cheat'}>
                                {"Cheat"}
                            </option>
                            <option key={2} value={'Buffh'}>
                                {"Buffh"}
                            </option>
                            <option key={0} value={'Pending'}>
                                {"Pending"}
                            </option>
                        </Input>
                    </div>
                </div>

                <div className="modal-footer">
                    <button type="button" onClick={dismissModal} className="btn btn-light" >Thoát</button>
                    <button type="button" onClick={updateUser} className="btn btn-primary">Lưu thông tin mới</button>
                </div>
            </div>

        </Modal>
    )
}

export default EditMulti
