import 'bootstrap/dist/css/bootstrap.min.css'

import {Form, FormGroup, Input, Label, Modal} from 'reactstrap'
import {KTSVG, toAbsoluteUrl} from '_metronic/helpers'
import React, {useEffect, useState} from 'react'
import {shallowEqual, useSelector} from 'react-redux'

import ChannelItem from './ChannelItem'
import {Group} from '../../models/Order'
import {RootState} from 'setup'
import {actions} from '../../redux/OrdersRedux'
import {useDispatch} from 'react-redux'

type Props = {
  show: boolean
  close: () => void
}

const STEP_INIT = 0
const STEP_PROCESSING = 1

const AddModal: React.FC<Props> = ({show, close}) => {
  const role: string =
    (useSelector<RootState>(({auth}) => auth.user?.role, shallowEqual) as string) || ''
  const balance: number =
    (useSelector<RootState>(({auth}) => auth.user?.balance, shallowEqual) as number) || 0
  const groups: Group[] =
    (useSelector<RootState>(({orders}) => orders.groups, shallowEqual) as Group[]) || []

  const dispatch = useDispatch()
  const [adding, setAdding] = useState(false)
  const [max_thread, setMax_thread] = useState(5)
  const [channel_id, setChannel_id] = useState('')
  //
  const [view_need, setView_need] = useState(1000)
  const [premium_rate, setPremium_rate] = useState(5)
  const [view_percent, setView_percent] = useState(30)
  //
  const [home_rate, setHome_rate] = useState(25)
  const [search_rate, setSearch_rate] = useState(25)
  const [suggest_rate, setSuggest_rate] = useState(25)
  const [direct_rate, setDirect_rate] = useState(25)
  //
  const [like_rate, setLike_rate] = useState(25)
  const [comment_rate, setComment_rate] = useState(25)
  const [mobile_rate, setMobile_rate] = useState(0)
  //
  const [note, setNote] = useState('')
  const [keyword, setKeyword] = useState('')
  const [comment_list, setComment_list] = useState('')
  const [group_id, setGroup] = useState(0)
  //
  const [enabled, setEnabled] = useState(1)

  const [step, setStep] = useState(STEP_INIT)

  const dismissModal = () => {
    dispatch(actions.clearcurrentOrder())
  }

  const clearData = () => {
    // setStep(STEP_INIT);
    // setAdding(false);
    // setMax_thread(5);
    // setChannel_id('');
    // setView_need(1000);
    // setPremium_rate(5);
  }

  const submit = () => {
    if (view_need % 100 !== 0 || view_need == 0) {
      alert('Số sub cần chạy phải chia hết cho 100')
      return
    }
    if (channel_id.trim() === '') {
      alert('Điền danh sách kênh cần chạy')
      return
    }
    if (home_rate + search_rate + suggest_rate + direct_rate != 100) {
      alert('Tổng nguồn view không đúng')
      return
    }
    if (like_rate > 100 || like_rate < 0) {
      alert('Tỉ lệ like không đúng')
      return
    }
    if (view_percent > 100 || view_percent < 0) {
      alert('phần trăm xem video không đúng')
      return
    }
    if (comment_rate > 100 || comment_rate < 0) {
      alert('Tỉ lệ comment không đúng')
      return
    }
    setAdding(true)
    setStep(STEP_PROCESSING)
    return
  }

  const processing = step === STEP_PROCESSING

  console.log('processing', processing)

  const _renderChannelInput = () => {
    return (
      <Input
        id='list_id'
        name='list_id'
        className='form-control form-control-solid'
        placeholder={role === 'ROLE_ADMIN' ? 'Mỗi id 1 dòng' : 'Id kênh'}
        value={channel_id}
        type={role === 'ROLE_ADMIN' ? 'textarea' : 'text'}
        onChange={(e) => setChannel_id(e.target.value)}
        style={{height: '200px'}}
      />
    )
  }

  const _renderChannelProcessItem = () => {
    const arrChannel = channel_id.split('\n')
    console.log('_renderChannelProcessItem', arrChannel)
    if (arrChannel.length) {
      return arrChannel.map((channel, index) => {
        if (channel)
          return (
            <li className='list-group-item' key={`channel_${index}`}>
              <ChannelItem
                {...{
                  channel,
                  home_rate,
                  note,
                  direct_rate,
                  view_need,
                  comment_rate,
                  mobile_rate,
                  search_rate,
                  enabled,
                  max_thread,
                  group_id,
                  like_rate,
                  comment_list,
                  suggest_rate,
                  premium_rate,
                  keyword,
                  view_percent,
                }}
                index={index}
              />
            </li>
          )
      })
    }
    return ''
  }

  return (
    <Modal
      unmountOnClose
      size='xl'
      isOpen={show}
      modalTransition={{timeout: 500}}
      onClosed={clearData}
    >
      <div className='modal-header'>
        <h5 className='modal-title'>Thêm nhiệm vụ</h5>
        <div className='btn btn-icon btn-sm btn-active-light-primary ms-2' aria-label='Close'>
          <span className='svg-icon svg-icon-2x'></span>
        </div>
      </div>
      <div className='modal-body'>
        <p>Cài đặt danh sách đơn</p>
        <Form>
          <div className='row'>
            <div className='col-6'>
              <FormGroup>
                <Label for='exampleEmail' className='required form-label'>
                  Danh sách id kênh cần chạy sub
                </Label>
                {step === STEP_INIT ? (
                  _renderChannelInput()
                ) : (
                  <div className='card border'>
                    <div className='card-body'>
                      <ul className='list-group list-group-flush'>{_renderChannelProcessItem()}</ul>
                    </div>
                  </div>
                )}
              </FormGroup>
              <FormGroup disabled={processing}>
                <Label for='exampleEmail' className='required form-label'>
                  Ghi chú
                </Label>
                <Input
                  disabled={processing}
                  id='note'
                  name='note'
                  value={note}
                  onChange={(e) => setNote(e.target.value)}
                  className='form-control form-control-solid'
                  type={'text'}
                />
              </FormGroup>
              <FormGroup disabled={processing}>
                <Label for='exampleEmail' className='required form-label'>
                  Danh sách comment
                </Label>
                <Input
                  disabled={processing}
                  id='comment_list'
                  name='comment_list'
                  value={comment_list}
                  onChange={(e) => setComment_list(e.target.value)}
                  className='form-control form-control-solid'
                  style={{height: '200px'}}
                  type={'textarea'}
                />
              </FormGroup>
            </div>
            <div className='col-6'>
              <p>Số lượng chạy</p>
              <div className='row'>
                <div className='flex flex-row justify-between space-x-3'>
                  <FormGroup disabled={processing}>
                    <Label for='exampleEmail' className='required form-label'>
                      View max
                    </Label>
                    <Input
                      disabled={processing}
                      id='view_need'
                      name='view_need'
                      value={view_need}
                      className='form-control form-control-solid'
                      placeholder='ví dụ : 1000'
                      onChange={(e) => setView_need(parseInt(e.target.value))}
                      type='number'
                    />
                  </FormGroup>
                  <FormGroup disabled={processing}>
                    <Label for='exampleEmail' className='required form-label'>
                      Luồng max
                    </Label>
                    <Input
                      id='max_thread'
                      name='max_thread'
                      value={max_thread}
                      disabled={processing || (role === 'ROLE_ADMIN' ? false : true)}
                      onChange={(e) => setMax_thread(parseInt(e.target.value))}
                      className='form-control form-control-solid'
                      placeholder='ví dụ : 1000'
                      type='number'
                    />
                  </FormGroup>
                  <FormGroup disabled={processing}>
                    <Label for='exampleEmail' className='required form-label'>
