/* eslint-disable jsx-a11y/anchor-is-valid */

import React,{useEffect} from 'react'
import {ComputerModel} from 'app/modules/history/models/Account'
import ComputerItem from './components/ComputerItem'
import { useDispatch, useSelector, shallowEqual } from 'react-redux'
import { actions } from './redux/AccountRedux'
import { RootState } from 'setup'
type Props = {
  className: string,
}

const ComputerList: React.FC<Props> = ({ className }) => {
  const dispatch = useDispatch()

  useEffect(() => {
    dispatch(actions.requestComputers())
  }, [])

  const computers:ComputerModel[] = useSelector<RootState>(({ histories }) => histories.computers, shallowEqual) as ComputerModel[] || []
  let sum_total=0;
  for (var i = 0; i < computers.length; i++) {
    sum_total=sum_total+computers[i].total;
  }
  return (
    <div className={`card ${className}`}>
      {/* begin::Header */}
      <div className='card-header border-0 pt-5'>
        <h3 className='card-title align-items-start flex-column'>
          <span className='card-label fw-bolder fs-3 mb-1'>Danh sách máy đang chạy</span>
          <span className='badge badge-warning'>Có {computers?computers.length:0} - Tổng {sum_total} luồng</span>
        </h3>
      </div>
      {/* end::Header */}
      {/* begin::Body */}
      <div className='card-body py-3'>
        {/* begin::Table container */}
        <div className='table-responsive'>
          {/* begin::Table */}
          <table className='table table-row-dashed table-row-gray-300 align-middle gs-0 gy-4'>
            {/* begin::Table head */}
            <thead>
              <tr>
                <th className='min-w-30px'>#</th>
                <th className='min-w-140px'>VPS IP</th>
                <th className='min-w-140px'>Số luồng đang chạy</th>
                <th className='min-w-140px'>Lần cuối</th>
                <th className='min-w-120px'>Hành động</th>
              </tr>
            </thead>
            {/* end::Table head */}
            {/* begin::Table body */}
            <tbody>
              {
                computers&&computers?.map((item: ComputerModel,index:number) => {
                  return <ComputerItem key={"computer-"+index} item={item} index={index}/>
                })
              }

            </tbody>
            {/* end::Table body */}
          </table>
          {/* end::Table */}
        </div>
        {/* end::Table container */}
      </div>
      {/* begin::Body */}
    </div>
  )
}

export default ComputerList
