import { Action } from '@reduxjs/toolkit'
import { App } from 'app/App'
import { persistReducer } from 'redux-persist'
import storage from 'redux-persist/lib/storage'
import { put, takeLatest } from 'redux-saga/effects'
import { OrderModel, OrderPost,OrderFormManual, OrderForm, Group, OrderUpdateForm } from '../models/Order'
import { getListOrder, updateOrder, addOrder, getListGroups, addGroup,addOrderManual, deleteHistoryError, deleteGroup, updateSetting, deleteChannel, addOrderMulti, getSetting } from './OrdersCRUD'
const sleep = (milliseconds: number) => {
  return new Promise(resolve => setTimeout(resolve, milliseconds))
}
export interface ActionWithPayload<T> extends Action {
  payload?: T
}

export const actionTypes = {
  RequestOrders: '[Orderdone] Requested',
  RequestSetting: '[Setting] Requested',
  RequestRemoveError: '[Setting] Request Remove Error',
  RequestUpdateSetting: '[Setting] Request Update Setting',
  UpdateSettingSuccess: '[Setting] Update Setting Success',
  RequestSettingSuccess: '[Setting] Request Setting Success',
  OrdersLoadedSuccess: '[Orderdone] Loaded succcess',
  OrdersLoadedFail: '[Orderdone] load fail',
  AddOrderRequest: '[Orderdone] Add Order Request',
  AddOrderManualRequest: '[Orderdone] Add Order Manual Request',
  AddOrderSuccess: '[Orderdone] Add Order Success',
  AddOrdersSuccess: '[Orderdone] Add Orders Success',
  AddOrderFail: '[Orderdone] Add Order Fail',
  ShowcurrentOrder: '[Orderdone] Show Account',
  RequestUpdate: '[Orderdone] Requested Update',
  UpdateMultiOrderRequest: '[Orderdone] Update Multi Order Request',
  UpdateSuccess: '[Orderdone] Update Success',
  UpdateMultiSuccess: '[Orderdone] Update Multi Success',
  UpdateFail: '[Orderdone] Update Fail',
  ClearSelected: '[Orderdone] Clear selected account',
  GroupLoadedRequest: '[Orderdone] Group Loaded Request',
  GroupLoadedSuccess: '[Orderdone] Group Loaded Success',
  GroupLoadedFail: '[Orderdone] Group Loaded Fail',
  GroupAddRequest: '[Orderdone] GroupAddRequest',
  GroupAddSuccess: '[Orderdone] Group Add Success',
  GroupAddFail: '[Orderdone] Group Add Fail',
  GroupDeleteRequest: '[Orderdone] Group Delete Request',
  GroupDeleteSuccess: '[Orderdone] Group Delete Success',
  SelectGroup: '[Orderdone] Select Group',
  DeleteOrderRequest: '[Orderdone] Delete Order Request',
  DeleteOrderSuccess: '[Orderdone] Delete Order Success',
  CheckedChange: '[Orderdone] Checked Change',
  CheckedAllChange: '[Orderdone] Checked All Change',
}

const initialorderstate: Iorderstate = {
  orders: [],
  loading: false,
  adding: false,
  groups: [],
  currentOrder: undefined,
  currentGroup: undefined,
  channel_prior: 0,

}

export interface Iorderstate {
  orders: OrderModel[]
  loading: boolean
  adding: boolean
  currentOrder?: OrderModel
  groups: Group[]
  currentGroup?: Group
  channel_prior: number
}

export const reducer = persistReducer(
  { storage, key: 'v1-orders', whitelist: [] },
  (state: Iorderstate = initialorderstate, action: ActionWithPayload<any>) => {
    switch (action.type) {
      case actionTypes.RequestOrders: {
        return {
          ...state,
          orders: [],
          loading: true
        }
      }
      case actionTypes.OrdersLoadedSuccess: {
        return {
          ...state,
          orders: action.payload?.orders || [],
          loading: false
        }
      }
      case actionTypes.GroupLoadedSuccess: {
        return {
          ...state,
          groups: action.payload?.groups
        }
      }
      case actionTypes.SelectGroup: {
        return {
          ...state,
          currentGroup: action.payload?.group
        }
      }
      case actionTypes.GroupDeleteSuccess: {
        return {
          ...state,
          currentGroup: undefined,
          groups: state.groups.filter((item: Group) => {
            if (item.id === action.payload?.id) {
              return false
            }
            return true
          })
        }
      }
      case actionTypes.DeleteOrderSuccess: {
        return {
          ...state,
          orders: state.orders.filter((item: OrderModel) => {
            if (item.videoid === action.payload?.videoid) {
              return false
            }
            return true
          })
        }
      }
      case actionTypes.OrdersLoadedFail: {
        return {
          ...state,
          orders: [],
          loading: false
        }
      }
      case actionTypes.RequestUpdate: {
        return {
          ...state,
          loading: true
        }
      }
      case actionTypes.UpdateMultiOrderRequest: {
        return {
          ...state,
          adding: true
        }
      }
      case actionTypes.UpdateSuccess: {
        const remaporders = state.orders.map((item: OrderUpdateForm) => {
          if (item.videoid === action.payload?.order?.videoid) {
            return action.payload?.order
          } else {
            return item
          }
        })
        return {
          ...state,
          orders: remaporders,
          loading: false,
          currentOrder: undefined
        }
      }
      case actionTypes.UpdateMultiSuccess: {
        const remaporders = state.orders.map((item: OrderModel) => {
          const findItem = action.payload?.videos.find((_item:OrderModel)=>{
            if(_item.videoid===item.videoid){
              return true
            }
            return false
          })
          if(findItem){
            return findItem
          }
          return item
        })
        return {
          ...state,
          orders: remaporders,
          loading: false,
          adding: false,
          currentOrder: undefined
        }
      }
      case actionTypes.UpdateFail: {
        return {
          ...state,
          loading: false
        }
      }
      case actionTypes.AddOrderRequest: {
        return {
          ...state,
          adding: true
        }
      }
      case actionTypes.AddOrderManualRequest: {
        return {
          ...state,
          adding: true
        }
      }
      case actionTypes.GroupAddSuccess: {
        return {
          ...state,
          groups: state.groups.concat(action.payload?.group)
        }
      }
      case actionTypes.AddOrderSuccess: {
        return {
          ...state,
          adding: false,
          orders: state.orders.concat(action.payload?.order)
        }
      }
      case actionTypes.AddOrdersSuccess: {
        return {
          ...state,
          adding: false,
          orders: state.orders.concat(action.payload?.orders)
        }
      }
      case actionTypes.RequestSettingSuccess: {
        return {
          ...state,
          channel_prior: action.payload.channel_prior
        }
      }
      case actionTypes.AddOrderFail: {
        return {
          ...state,
          adding: false,
          message: action.payload?.message
        }
      }
      case actionTypes.ShowcurrentOrder: {
        return {
          ...state,
          currentOrder: action.payload?.currentOrder
        }
      }
      case actionTypes.ClearSelected: {
        return {
          ...state,
          currentOrder: action.payload?.currentOrder
        }
      }
      case actionTypes.CheckedChange: {
        return {
          ...state,
          orders:  state.orders.map(item=>{
            if(item.videoid===action.payload?.data?.videoid){
              return {
                ...item,
                checked:action?.payload?.data?.checked
              }
            }
            return item
          })
        }
      }
      case actionTypes.CheckedAllChange: {
        return {
          ...state,
          orders:  state.orders.map(item=>{
            return {
              ...item,
              checked:action?.payload?.checked
            }
          })
        }
      }



      default:
        return state
    }
  }
)

export const actions = {
  requestOrders: () => ({ type: actionTypes.RequestOrders }),
  requestSetting: () => ({ type: actionTypes.RequestSetting }),
  requestRemoveError: () => ({ type: actionTypes.RequestRemoveError }),
  requestUpdateSetting: (channel_prior: number) => ({ type: actionTypes.RequestUpdateSetting, payload: { channel_prior } }),
  fulfillorders: (orders: OrderModel[]) => ({ type: actionTypes.OrdersLoadedSuccess, payload: { orders } }),
  loadordersFail: (message: string) => ({ type: actionTypes.OrdersLoadedFail, payload: { message } }),
  fillSetting: (channel_prior: number) => ({ type: actionTypes.RequestSettingSuccess, payload: { channel_prior } }),
  addOrderRequest: (data: OrderForm) => ({ type: actionTypes.AddOrderRequest, payload: { data } }),
  addOrderManualRequest: (data: OrderFormManual) => ({ type: actionTypes.AddOrderManualRequest, payload: { data } }),
